  // ----------------------------------------------------------------------
  // Port handler base-class functions for typed input ports
  //
  // Call these functions directly to bypass the corresponding ports
  // ----------------------------------------------------------------------

  void LinuxUartDriverComponentBase ::
    recvReturnIn_handlerBase(
        FwIndexType portNum,
        Fw::Buffer& fwBuffer
    )
  {
    // Make sure port number is valid
    FW_ASSERT(
      (0 <= portNum) && (portNum < this->getNum_recvReturnIn_InputPorts()),
      static_cast<FwAssertArgType>(portNum)
    );

    // Lock guard mutex before calling
    this->lock();

    // Call handler function
    this->recvReturnIn_handler(
      portNum,
      fwBuffer
    );

    // Unlock guard mutex
    this->unLock();
  }

  void LinuxUartDriverComponentBase ::
    run_handlerBase(
        FwIndexType portNum,
        U32 context
    )
  {
    // Make sure port number is valid
    FW_ASSERT(
      (0 <= portNum) && (portNum < this->getNum_run_InputPorts()),
      static_cast<FwAssertArgType>(portNum)
    );

    // Call handler function
    this->run_handler(
      portNum,
      context
    );
  }

  Drv::ByteStreamStatus LinuxUartDriverComponentBase ::
    send_handlerBase(
        FwIndexType portNum,
        Fw::Buffer& sendBuffer
    )
  {
    // Make sure port number is valid
    FW_ASSERT(
      (0 <= portNum) && (portNum < this->getNum_send_InputPorts()),
      static_cast<FwAssertArgType>(portNum)
    );

    Drv::ByteStreamStatus retVal;

    // Lock guard mutex before calling
    this->lock();

    // Call handler function
    retVal = this->send_handler(
      portNum,
      sendBuffer
    );

    // Unlock guard mutex
    this->unLock();

    return retVal;
  }

