  // ----------------------------------------------------------------------
  // Command handler base-class functions
  //
  // Call these functions directly to bypass the command input port
  // ----------------------------------------------------------------------

  void SystemResourcesComponentBase ::
    ENABLE_cmdHandlerBase(
        FwOpcodeType opCode,
        U32 cmdSeq,
        Fw::CmdArgBuffer& args
    )
  {
    // Deserialize the arguments
    Fw::SerializeStatus _status = Fw::FW_SERIALIZE_OK;

    // Reset the buffer
    args.resetDeser();

    Svc::SystemResourceEnabled enable;
    _status = args.deserializeTo(enable);
    if (_status != Fw::FW_SERIALIZE_OK) {
      if (this->m_CmdStatus_OutputPort[0].isConnected()) {
        this->m_CmdStatus_OutputPort[0].invoke(
          opCode,
          cmdSeq,
          Fw::CmdResponse::FORMAT_ERROR
        );
      }
      return;
    }

#if FW_CMD_CHECK_RESIDUAL
    // Make sure there was no data left over.
    // That means the argument buffer size was incorrect.
    if (args.getDeserializeSizeLeft() != 0) {
      if (this->m_CmdStatus_OutputPort[0].isConnected()) {
        this->m_CmdStatus_OutputPort[0].invoke(
          opCode,
          cmdSeq,
          Fw::CmdResponse::FORMAT_ERROR
        );
      }
      return;
    }
#endif

    this->lock();

    this->ENABLE_cmdHandler(
      opCode, cmdSeq,
      enable
    );

    this->unLock();
  }

